/**
 * 数值格式化
 * @param {*} num 数值
 * @param {*} precision=2 精度
 * @param {*} thousands=',' 千位分隔符
 * @param {*} prefix 数值前缀
 * @returns 格式化后的数值
 */
export function numFormat(num, precision = 2, thousands = ',', prefix = '') {
  let value = num.toString().trim()
  value = value.replace(/,/g, '')
  if (!value) return ''
  precision && (value = parseFloat(value).toFixed(precision))
  thousands && (value = value.replace(/\B(?=(\d{3})+(?!\d))/g, thousands))
  return prefix + value
}
